using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Windows;
using System.Windows.Controls;
using System.Windows.Data;
using System.Windows.Documents;
using System.Windows.Input;
using System.Windows.Media;
using System.Windows.Media.Imaging;
using System.Windows.Shapes;

namespace ClientGui
{
    /// <summary>
    /// Interaction logic for SinglePlayerWindow.xaml
    /// </summary>
    public partial class SinglePlayerWindow : Window
    {
        MazeDisplay mazeDisplay;
        public SinglePlayerWindow()
        {
            InitializeComponent();
            GetMazeInfo();
            AddGrid();
        }
        /// <summary>
        /// gets maze from server
        /// </summary>
        public void GetMazeInfo()
        {
            MazeInfo mazeInfo = new MazeInfo(40, 50, 36, 34, 8, 48, 36, 34, "");
            mazeDisplay = new MazeDisplay(mazeInfo);
        }
        public void AddGrid()
        {
            Grid maze = mazeDisplay.GetMazeToGrid(true, false);
            windowBackground.Children.Add(maze);
        }
        private void back_Click(object sender, RoutedEventArgs e)
        {
            this.Close();
        }

        private void hint_Click(object sender, RoutedEventArgs e)
        {
            mazeDisplay.GetHint();
        }

        private void Window_KeyDown(object sender, KeyEventArgs e)
        {
            if (e.Key == Key.Left)
            {
                mazeDisplay.MovePlayer(-1, 0);
            }
            if (e.Key == Key.Right)
            {
                mazeDisplay.MovePlayer(1, 0);
            }
            if (e.Key == Key.Up)
            {
                mazeDisplay.MovePlayer(0, -1);
            }
            if (e.Key == Key.Down)
            {
                mazeDisplay.MovePlayer(0, 1);
            }
        }
    }
}
